import React from 'react';

import personService from '../services/persons'

const Person = ({ person, setPersons, setFilteredPersons }) => {
    const resetList = (setLists, personList) => {
        setLists.map(setList => {
            return(
                <>
                    {setList([])}
                    {setList([personList])}
                </>
            )
        })
    }

    const addPersonList = (personList) => {
        resetList({setPersons, setFilteredPersons}, personList)
    }

    const deletePerson = personID => {
        personService
            .deleteObject(personID)
            .then(modifiedPersonList => {
                addPersonList(modifiedPersonList)
            })
    }

    return(
        <>
            <p>
                {person.name} {person.number}
                <button onClick={() => deletePerson(person.id)}>delete</button>
            </p>
        </>
    )
}

export default Person
