import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-1234567' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ filteredPersons, setFilteredPersons ] = useState([...persons])

  const handleNameChange = (event) => {
    // console.log(event.target.value);
    setNewName(event.target.value);
  }

  const handleNumberChange = (event) => {
    // console.log(event.target.value);
    setNewNumber(event.target.value);
  }

  const handleSearch = (event) => {
    let searchEntry = (event.target.value).toLowerCase()

    if(searchEntry.length > 0) {
      setFilteredPersons(persons.filter(person => person.name.toLowerCase().includes(searchEntry)))
    } else {
      setFilteredPersons([...persons])
    }
  }

  const checkDuplicatePerson = (person) => {
    // if (persons.find(p => p.name === person.name)) {
    //   return true
    // }
    // return false
    persons.find(p => p.name === person.name) ? true : false
  }

  const addPerson = (event) => {
    const alertText = `${newName} already exists in the phonebook`
    event.preventDefault();
    const personObject = {
      name: newName,
      number: newNumber
    }
    checkDuplicatePerson(personObject) ? alert(alertText) : setPersons(persons.concat(personObject))
    setNewName('');
    setNewNumber('');
  }

  return (
    <div>
      <h2>Phonebook</h2>
      filter shown with <input onChange={handleSearch} />
      <h2>add a new</h2>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {filteredPersons.map(person => {
        return <p key={person.name}>{person.name} {person.number}</p>
      })}
    </div>
  )
}

export default App
