import React, { useState } from 'react'

const Header = ({ text }) => <h2>{text}</h2> 

const SearchFilter = ({ text, handleSearch }) => <div>{text} <input onChange={handleSearch} /></div>

const AddPeopleForm = ({ handleNameChange, handleNumberChange, addPerson, newName, newNumber }) => {

  return (
    <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
  )
}

const Person = () => {}

const Persons = () => {}

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-1234567' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ filteredPersons, setFilteredPersons ] = useState([...persons])

  const handleNameChange = (event) => setNewName(event.target.value);

  const handleNumberChange = (event) => setNewNumber(event.target.value); 

  const handleSearch = (event) => {
    let searchEntry = (event.target.value).toLowerCase()

    if(searchEntry.length > 0) {
      setFilteredPersons(persons.filter(person => person.name.toLowerCase().includes(searchEntry)))
    } else {
      setFilteredPersons([...persons])
    }
  }

  const checkDuplicatePerson = (person) => {
    if (persons.find(p => p.name === person.name)) {
      return true
    }
    return false
  }

  const concatPersonLists = (personObject) => {
    setPersons(persons.concat(personObject))
    setFilteredPersons(persons.concat(personObject))
  }

  const addPerson = (event) => {
    const alertText = `${newName} already exists in the phonebook`
    event.preventDefault();
    const personObject = {
      name: newName,
      number: newNumber
    }
    checkDuplicatePerson(personObject) ? alert(alertText) : concatPersonLists(personObject)
    setNewName('');
    setNewNumber('');
  }

  return (
    <div>
      <Header text="Phonebook" />
      <SearchFilter text="filter shown with" handleSearch={handleSearch} />
      <Header text="add a new" />
      <AddPeopleForm handleNameChange={handleNameChange} handleNumberChange={handleNumberChange} addPerson={addPerson} newName={newName} newNumber={newNumber} />
      <Header text="Numbers" />
      {filteredPersons.map(person => {
        return <p key={person.name}>{person.name} {person.number}</p>
      })}
    </div>
  )
}

export default App
